const modalFields = interaction.fields

// You can do whatever you want with this information
// In this example, the bot simply replies to modal submit trigger
// and returns list of modal fields

await reply({
  content: `Modal submitted: ${interaction.customId}`,
  embeds: [
    {
      fields: modalFields.map(field => {
        return {
          name: field.customId,
          value: field.value || '\u200B'
        }
      })
    }
  ]
})
